class TaskManager{constructor(){this.tasks=[]}addTask(task){this.tasks.push(task),this.renderTasks()}removeTask(index){this.tasks.splice(index,1),this.renderTasks()}renderTasks(){let taskList=document.getElementById("task-list");taskList.innerHTML="",this.tasks.forEach((task,index)=>{var taskItem=document.createElement("li"),task=(taskItem.textContent=task,document.createElement("button"));task.textContent="Remover",task.onclick=()=>this.removeTask(index),taskItem.appendChild(task),taskList.appendChild(taskItem)})}}let taskManager=new TaskManager;document.getElementById("add-task-button").addEventListener("click",()=>{var taskInput=document.getElementById("task-input"),task=taskInput.value.trim();task&&(taskManager.addTask(task),taskInput.value="")});